static void test_cmp_pos(context_t* ctx) {
  printf("cmp works for positive numbers?\n");

  expect(integer_gt(integer_from_small(54), integer_from_small(45), ctx), true);
  expect(integer_gt(integer_from_small(45), integer_from_small(54), ctx), false);
  expect(integer_gt(integer_from_small(45), integer_from_small(45), ctx), false);
  expect(integer_gt(integer_from_str("5498765432109876",ctx), integer_from_str("4598765432109876",ctx), ctx), true);
  expect(integer_gt(integer_from_str("4598765432109876",ctx), integer_from_str("5498765432109876",ctx), ctx),false);
  expect(integer_gt(integer_from_str("4598765432109876",ctx), integer_from_str("4598765432109876",ctx), ctx),false);
}

