static integer_t factorial(integer_t n, context_t* ctx) {
  dup_integer_t(n);
  if (integer_eq(n,integer_from_small(0), ctx)) {
    drop_integer_t(n, ctx);
    return integer_from_small(1);
  }
  dup_integer_t(n);
  if (integer_eq(n, integer_from_small(1), ctx)) {
    drop_integer_t(n, ctx);
    return integer_from_small(1);
  }
  dup_integer_t(n);
  return integer_mul(factorial(integer_dec(n, ctx), ctx),n, ctx);
}

static void test_large(context_t* ctx) {
  const char* tenFactorial = "3628800";
  const char* hundredFactorial = "93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000";
  const char* threeToTenThousand = "";

  expect_eq(factorial(integer_from_small(10), ctx),integer_from_str(tenFactorial, ctx),ctx);
  expect_eq(factorial(integer_from_small(100), ctx),integer_from_str(hundredFactorial, ctx),ctx);
  expect_eq(integer_pow(integer_from_small(3),integer_from_int(10000,ctx), ctx), integer_from_str(threeToTenThousand, ctx),ctx);

  // large multiply divide
  integer_t x = integer_from_str(hundredFactorial, ctx);
  expect_eq(integer_div(integer_mul(dup_integer_t(x), dup_integer_t(x), ctx),dup_integer_t(x), ctx), x,ctx);
  x = integer_from_str(threeToTenThousand, ctx);
  expect_eq(integer_div(integer_mul(dup_integer_t(x), dup_integer_t(x), ctx), dup_integer_t(x), ctx), x,ctx);
  integer_t y = integer_from_str(hundredFactorial, ctx);
  x = integer_from_str(threeToTenThousand, ctx);
  expect_eq(integer_div(integer_mul(dup_integer_t(y), dup_integer_t(x), ctx), y, ctx), x,ctx);
}

