static void test_count(context_t* ctx) {
  expect_eq(integer_count_digits(integer_from_int(0, ctx), ctx), integer_from_int(1, ctx),ctx);
  expect_eq(integer_count_digits(integer_from_int(9999,ctx), ctx), integer_from_int(4, ctx),ctx);
  expect_eq(integer_count_digits(integer_from_int(70123,ctx), ctx), integer_from_int(5, ctx),ctx);
  expect_eq(integer_count_digits(integer_from_int(-70123, ctx), ctx), integer_from_int(5, ctx),ctx);
  expect_eq(integer_count_digits(integer_from_str("1234567890",ctx), ctx), integer_from_int(10, ctx),ctx);
  expect_eq(integer_count_digits(integer_neg(integer_from_str(b, ctx), ctx), ctx), integer_from_int(100, ctx),ctx);

  expect_eq(integer_ctz(integer_from_int(0,ctx), ctx), integer_from_int(0,ctx),ctx);
  expect_eq(integer_ctz(integer_from_int(-9900, ctx), ctx), integer_from_int(2,ctx),ctx);
  expect_eq(integer_ctz(integer_from_int(70000,ctx), ctx), integer_from_int(4,ctx),ctx);
  expect_eq(integer_ctz(integer_from_str("100000000",ctx), ctx), integer_from_int(8,ctx),ctx);
  expect_eq(integer_ctz(integer_from_str("10000000000",ctx), ctx), integer_from_int(10,ctx),ctx);
  expect_eq(integer_ctz(integer_from_str("1000000000000000000",ctx), ctx), integer_from_int(18,ctx),ctx);
}

static void test_pow10(context_t* ctx) {
  expect_eq(integer_mul_pow10(integer_from_str("1234567890",ctx),integer_from_int(0,ctx), ctx), integer_from_str("1234567890",ctx),ctx);
  expect_eq(integer_mul_pow10(integer_from_int(0,ctx), integer_from_str("1234567890",ctx), ctx), integer_from_int(0,ctx),ctx);
  expect_eq(integer_mul_pow10(integer_from_int(-1, ctx), integer_from_str("12",ctx), ctx), integer_from_str("-1e12",ctx),ctx);
  expect_eq(integer_mul_pow10(integer_from_str("1234567890",ctx), integer_from_int(8,ctx), ctx), integer_from_str("1234567890e8",ctx),ctx);
  expect_eq(integer_mul_pow10(integer_from_str("-1234567890",ctx), integer_from_int(8,ctx), ctx), integer_from_str("-1234567890e8",ctx),ctx);
  expect_eq(integer_mul_pow10(integer_from_str("1234567890",ctx), integer_from_int(18,ctx), ctx), integer_from_str("1234567890e18",ctx),ctx);
  expect_eq(integer_mul_pow10(integer_from_int(1234,ctx), integer_from_int(14,ctx), ctx), integer_from_str("1234e14",ctx),ctx);

  expect_eq(integer_div_pow10(integer_from_str("1234567890",ctx), integer_from_int(0,ctx), ctx), integer_from_str("1234567890",ctx),ctx);
  expect_eq(integer_div_pow10(integer_from_int(0,ctx), integer_from_str("1234567890",ctx), ctx), integer_from_int(0,ctx),ctx);
  expect_eq(integer_div_pow10(integer_from_str("-1e13",ctx), integer_from_str("12",ctx), ctx), integer_from_str("-10",ctx),ctx);
  expect_eq(integer_div_pow10(integer_from_str("1234567890",ctx), integer_from_int(8,ctx), ctx), integer_from_str("12",ctx),ctx);
  expect_eq(integer_div_pow10(integer_from_str("-1234567890",ctx), integer_from_int(8,ctx), ctx), integer_from_str("-12",ctx),ctx);
  expect_eq(integer_div_pow10(integer_from_str("9999999999",ctx), integer_from_int(8,ctx), ctx), integer_from_str("99",ctx),ctx);
  expect_eq(integer_div_pow10(integer_from_str("1234567890",ctx), integer_from_int(18,ctx), ctx), integer_from_int(0,ctx),ctx);
  expect_eq(integer_div_pow10(integer_from_str("1234e14",ctx), integer_from_int(14,ctx), ctx), integer_from_str("1234",ctx),ctx);
}

