static integer_t init_num(size_t  digits, context_t* ctx) {
  char* s = (char*)runtime_malloc(digits + 1, ctx);
  for (size_t i = 0; i < digits; i++) {
    s[i] = '0' + (9 - (i%10));
  }
  s[digits] = 0;
  integer_t x = integer_from_str(s,ctx);
  runtime_free(s);
  return x;
}

