static void test_bitcount(void) {
  uint32_t values[] = { 1,0x80000000,0xFFFFFFFF,0xFFFF,0xFFFF0000,0x7FFFFFFF,0xFFFFFFFE, 0x7FFFFFFE, 0x80000001, 0 };
  size_t i = 0;
  uint32_t v;
  uint8_t l, t;
  do {
    v = values[i++];
    l = bits_clz32(v);
    t = bits_ctz32(v);
    if (v == 0) assert((l + t) == 64);
    printf("value: 0x%08x, ctz: %2u, clz: %2u\n", v, t, l);
  } while (v != 0);
  for (v = 1; v != 0; v <<= 1) {
    l = bits_clz32(v);
    t = bits_ctz32(v);
    printf("value: 0x%08x, ctz: %2u, clz: %2u\n", v, t, l);
    assert((l + t) == 31);
  }
}

static void test_popcount(void) {
  printf("testing popcount..."); fflush(stdout);
  for (uint32_t i = 0; i < UINT32_MAX; i++) {
    uint32_t c1 = bits_generic_count32(i);
    uint32_t c2 = bits_count32(i);
    if (c1 != c2) {
      assert(c1 == c2);
      abort();
    }
    if (i % 10000000 == 0) {
      printf(".");
      fflush(stdout);
    }
  }
  printf(": ok for all 32-bit values.\n");
}

static void test_box_double(double dx, context_t* ctx) {
  box_t bx = box_double(dx, ctx);
  double e = unbox_double(bx, ctx);
  printf("value: %.20e, box-unbox to: %.20e, box: 0x%016zx\n", dx, e, bx.box);
  assert(e == dx || (isnan(e) && isnan(dx)));
}

static void test_double(context_t* ctx) {
  double values[] = { 0.0, 1.0, 3.142, 0.5, 1.5, 2.5, -1.5, -2.5, pow(2.0,-510.0), pow(2.0,-511.0), pow(2.0,32.0), pow(2.0,64.0), pow(2.0,511.0), pow(2.0,512.0), pow(2.0,513.0), HUGE_VALL, pow(10.0,308), DBL_MAX, -DBL_MAX, DBL_EPSILON, (double)NAN };
  size_t i = 0;
  double dx;
  do {
    dx = values[i++];
    integer_t x = integer_from_double(dx, ctx);
    printf("value: %.20e, %.20e, integer: ", dx, round(dx)); integer_print(x, ctx); printf("\n");
  } while (!isnan(dx));
  // test boxing
  i = 0;
  do {
    dx = values[i++];
    test_box_double(dx,ctx);
    test_box_double(nexttoward(dx,-HUGE_VALL), ctx);
    test_box_double(nexttoward(dx, HUGE_VALL), ctx);
    test_box_double(-dx, ctx);
    test_box_double(nexttoward(-dx, -HUGE_VALL), ctx);
    test_box_double(nexttoward(-dx, +HUGE_VALL), ctx);
  } while (!isnan(dx));
}

static void test_count10(context_t* ctx) {
  uint64_t u = 0;
  for (int i = 0; i < 22; i++) {
    uint8_t d1 = bits_digits64(u - 1);
    printf("value: %20llu, %3u", u-1, d1);
    uint8_t d0 = bits_digits64(u);
    printf(", value: %20llu, %3u", u, d0);
    uint8_t d9 = bits_digits64(u*9);
    printf(", value: %20llu, %3u\n", u*9, d9);
    if (u==0) u = 1;
         else u *= 10;
  }
  u = 1;
  for (int i = 0; i < 64; i++) {
    uint8_t d1 = bits_digits64(u - 1);
    printf("value: %20llu, %3u", u-1, d1);
    uint8_t d0 = bits_digits64(u);
    printf(", value: %20llu, %3u", u, d0);
    uint8_t d9 = bits_digits64(u*9);
    printf(", value: %20llu, %3u\n", u*9, d9);
    if (u==0) u = 1;
    else u <<= 1;
  }
}

static void test_random(context_t* ctx) {
  msecs_t start = _clock_start();
  uint32_t y = srandom_uint32(ctx);
  const size_t N = 100000000;
  for (size_t i = 0; i < N; i++) {
    y = srandom_range32(60000,ctx);
  }
  msecs_t end = _clock_end(start);
  printf("chacha20: final: 0x%x, %6.3fs\n", y, (double)end/1000.0);
}

static void test_ovf(context_t* ctx) {
  /*
  add + subtract, 100000000x
  on x86-64, with a Xeon:
         portable  builtin   (overflow detection method)
  msvc:  0.185s    N/A
  gcc :  0.147s    0.105s
  clang: 0.175s    0.170s

  add only, 100000000x
  on x86-64, with a Xeon:
         portable  builtin   (overflow detection method)
  msvc:  0.102s    N/A
  gcc :  0.068s    0.054s
  clang: 0.067s    0.053s
  */
  msecs_t start = _clock_start();
  integer_t n = integer_zero;
  intx_t i = 0;
#ifdef NDEBUG
  const intx_t delta = 1;
#else
  const intx_t delta = 100;
#endif
  for (; i < 100000000; i += delta) { n = integer_inc(n, ctx); }
  for (; i > 0; i -= delta) { n = integer_dec(n, ctx); }
  msecs_t end = _clock_end(start);
  integer_print(n, ctx);
  printf("\nint-inc-dec: %6.3fs\n", (double)end/1000.0);
}

